float rectangle(vec2 uv, vec2 pos, float width, float height) {
	return step(pos.x, uv.x) * step(uv.x, pos.x + width) * step(pos.y, uv.y) * step(uv.y, pos.y + height);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float scale = 0.5;
    //scale = iMouse.x / iResolution.x;

    vec2 position = vec2(0.5, 0.5);
    position = iMouse.xy / iResolution.xy;

    vec2 uv = fragCoord.xy / iResolution.xy;

    vec4 color = texture(iChannel0, (uv-position+0.25) / scale);

    float mask = rectangle(uv, position-0.5*scale, scale, scale);

    color = mix(texture(iChannel1, uv), color, mask);

    fragColor = color;
}
