// inspired from https://www.shadertoy.com/view/lljSDy

//#define rnd(p)	fract(sin( (p) * 412.9898  ) * 43758.5453 )
//#define rnd2(p)	fract(sin( dot(p, vec2(12.9898, 78.233) )  ) * 43758.5453 )
#define s(a)       ( sin(a) )
#define rnd(p)	   ( .5+.5*s(p+t)*s(p*2.17-t)*s(p*5.7+t) )
//#define rnd(p)   ( .5+.5*(s(p+t)+s(p*2.17-t)+s(p*5.7+t))/3. )

void mainImage( out vec4 o,  vec2 U )
{
    o -= o;
    float r=1., t=iTime, H = iResolution.y, id=1.;
    U /=  H;
    vec2 fU;  
    U*=.5;        ;                       // unzoom for the whole domain falls within [0,1]^n

    o.b = .25;                            // backgroud = cold blue

    for (int i=0; i<28; i++) {             // to the infinity, and beyond ! :-)
        fU = min(U,1.-U); if (min(fU.x,fU.y) < .3*r/H) { o--; break; } // cell border
        if (rnd(id) > .7) break; // cell is out of the shape

                // --- iterate to child cell
        fU = step(.5,U);                  // select child
#if 1
        id = 4.*id + 2.*fU.y+fU.x;
        U = 2.*U - fU;                    // go to new local frame
        r *= 2.; if (r>H) break;
        o += .13;                         // getting closer, getting hotter
#else
    #define split(a)  id = 2.*id + fU.a, U.a = 2.*U.a - fU.a
        if (i%2==0) 
             { split(y); r *= 2.;  }
        else { split(x); if (r>.5*H) break; o += .13;}
#endif             
    }

}
