renderpass.code:
#define S(v)   smoothstep( p, 0., v)             
               //  alpha-mask   alpha           pattern
#define D(l)   O += (1.-O.a) * S( l-.3 ) * vec4( vec3( S( abs(l -.3 -p/2.) ) ), 1)

void mainImage( out vec4 O, vec2 U )
{
	vec2 R = iResolution.xy, C;
    float N = 5.,                   // canvas size
          z = N + 1.,
          p = 8. * z / R.y;         // pixel size (for antialiasing)

	U = z * ( U - .5*R) / R.y;
    C = mod(round(U),2.);

    O -= O;

    U = fract(U+.5)*2.-1.;

    if (mod(C.x+C.y,2.)==0.) {      // crossing tile
        if (C.x == 0.) U.y = -U.y;  //   switch order
        D( abs( U.x - U.y ) );
        D( abs( U.x + U.y ) );
    }
    else {                          // corners tile
        U = 1.-abs(U);
        D( U.x + U.y ); 
    }

    O = 1.-O;
}
