float random(vec2 p) {
	return fract(sin(p.x * 269.5 + p.y * 183.3) * 43758.5453);
}

float noise2(vec2 point) {
	vec2 p = floor(point);
	vec2 f = fract(point);
    float x = smoothstep(0.0, 1.0, f.x);
	return mix(
		mix( random(p + vec2(0.0, 0.0)), random(p + vec2(1.0, 0.0)), x),
		mix( random(p + vec2(0.0, 1.0)), random(p + vec2(1.0, 1.0)), x),
		smoothstep(0.0, 1.0, f.y)
	);
}

float fractal(vec2 point) {
    float sum = 0.0;
    float scale = 0.5;
    float totalScale = 0.0;
    for (int i = 0; i < 4; i++) {
		sum += noise2(point) * scale;
		point *= 2.0;
        totalScale += scale;
        scale /= 2.0;
	}
	return sum / totalScale;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
	vec2 point = fragCoord.xy / iResolution.x;
    point.x += iTime * 0.1;
    float value = fractal(point * 10.0);
    fragColor = vec4(value, value, value, 1.0);
}
