float hash13(vec3 p3)
{
	p3  = fract(p3 * 10.32);
    p3 += dot(p3, p3.yzx + 19.19);
    return fract((p3.x + p3.y) * p3.z);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy*4.;
    float t = iTime + 0.1;
    float n = pow(hash13(vec3(floor(uv*1.),t))*tan(t),2.2)*.2;
    uv.x += n;	
    uv = fract(uv);

    float p = step(uv.x,.4);
    float q = 1.-step(uv.x,.2);

	fragColor = vec4(p,p-q,0.,1.);
}
