void mainImage( out vec4 f, in vec2 w ){
    float d=.1,e,t=iDate.w,a=cos(t),b=sin(t);
    vec4 v=vec4(0.);vec3 z,p,c=vec3(w,0.)/iResolution;
	for(int i=0;i<99;i++){
        z=mod(-d*vec3(c.x,1.+t/d,c.y),2.);
	    p=1.-z;
		e=.5*length(p)-.3;d+=e;
        if(e<1E-4){v=texture(iChannel0,p)*4.*(.2+a*p.z+b*p.x);break;}
    }
    f=v;
}
