#define S(b) length(p-o-(b)*clamp(dot(p-o,b) / dot(b,b), 0., 1.))

#define A(i) vec2(x.x+i, fract(sin(x+i+iTime)*1e3)*(9.-abs(x+i)))

void mainImage( out vec4 f, in vec2 w )
{
    vec2 p = w*18./iResolution.x - vec2(9, 1), x = floor(p)-.5, o=A(1.);
	f = vec4(2,1,2,1) - min(S(A(0.)-o), S(A(2.)-o))*4.;
}
