float noiseSpeed = 30.0;
float noiseClampNum = 1.0;
float noiseWaveNum = 32.0;
float noisePower = 0.05;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    float noise = fract( iTime * noiseSpeed ) - (1.0-uv.y);
    uv.x += sin( radians( clamp( noise * 360.0*noiseWaveNum,0.0,360.0 * noiseClampNum ) ) ) * noisePower;
    vec4 col = texture(iChannel0, uv );

	fragColor = col;    
}
