void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    float dst = distance(uv, vec2(0.5, 0.5));
    float wave = sin(sqrt(dst)*12.0-(iTime));

    vec3 da = texture(iChannel0, uv-vec2(0.01)).rgb; 
    vec3 db = texture(iChannel0, uv).rgb; 
    vec3 d;

    d = mix(da, db, wave*wave);

	fragColor = vec4(d,1.0);
}
