#define blur 0.004

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float angleY = sin(8.0*iTime);
   	float angleX = cos(4.0*iTime);

	vec2 uv = fragCoord.xy / iResolution.xy;    
    float dist = distance(uv, vec2(sin(0.3*iTime)));

    uv -= vec2(0.5, 0.5);
    uv.y += 0.1 * cos(2.0*iTime);

    uv *= dist;
    float fi = iTime/4.0;

//    fi *= 0.01*sin(dist);

    uv *= mat2(cos(fi), -sin(fi), sin(fi), cos(fi));
    uv *= mat2(1.0, tan(angleX*3.14/180.0), tan(angleY*3.14/180.0), 1.0);

    vec4 pixel = texture(iChannel0, uv+vec2(blur, 0.0)) +
        	     texture(iChannel0, uv-vec2(blur, 0.0)) +
                 texture(iChannel0, uv+vec2(0.0, blur)) +
                 texture(iChannel0, uv-vec2(0.0, blur)); 
    pixel.r += cos(2.0*iTime) * texture(iChannel1, uv).r;
    pixel.g += sin(2.0*iTime) * texture(iChannel1, uv).r;
    pixel.b += sin(4.0*iTime) * texture(iChannel1, uv).r;

	pixel /= 4.0;
	fragColor = pixel;
}
