const vec2 center = vec2(.5,.5);
#define threshold 0.3;
#define hueshift .01;
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord.xy / iResolution.xy;

    // first texture row is frequency data
    float fft  = texture( iChannel1, vec2(uv.x,.25) ).x + threshold; 

    // second texture row is the sound wave
    float wave = texture( iChannel1, vec2(uv.x,uv.y) ).x + threshold;

    uv -= center; 

    float m = length(center);

    float r = pow(length(uv),m) + pow(fft,.1) + pow(wave,.01) ;
    float theta = iDate.w * hueshift;
    //float theta = wave;

    float tX = r * cos(theta);
    float tY = r * sin(theta);
    vec2 tPos = vec2(tX,tY);

    fragColor = texture(iChannel0,tPos);
}
