vec2 random2( vec2 p ) {
    return fract(sin(vec2(dot(p,vec2(127.1,311.7)),dot(p,vec2(269.5,183.3))))*43758.5453);
}

float random( vec2 p) {
    return fract(sin(dot(p.xy,vec2(12.9898,78.233)))*43758.5453);
}





void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    vec3 color = vec3(.0);



    float pointRadius = 25.6;
    float pointCellWidth = 50.0;
    float pointRadiusOfCell = pointRadius / pointCellWidth;

    vec2 totalCellCount = iResolution.xy / pointCellWidth;
    vec2 cellUv = uv * totalCellCount;

    // Tile the space
    vec2 i_uv = floor(cellUv);
    vec2 f_uv = fract(cellUv);

    float m_dist = 1.;  // minimun distance




    for (int y= -1; y <= 1; y++) {
        for (int x= -1; x <= 1; x++) {
            // Neighbor place in the grid
            vec2 neighbor = vec2(float(x), float(y));

            // Random position from current + neighbor place in the grid
            vec2 point = random2(i_uv + neighbor);


            vec2 cellRowCol = floor(fragCoord / pointCellWidth) + neighbor;
            vec2 cellCenterFragCoord = ((cellRowCol * pointCellWidth) + (pointCellWidth / 2.0));
            vec2 cellCenterUv = cellCenterFragCoord / iResolution.xy;
            float cellSizeAdjust = random(cellCenterUv);


            // Animate the point
            point = 0.5 + 0.75 * sin(iTime + 6.2831 * point);

            // Vector between the pixel and the point
            vec2 diff = neighbor + point - f_uv;

            // Distance to the point
            float dist = length(diff);
            dist += cellSizeAdjust; // Effectively remove point

            // Keep the closer distance
            m_dist = min(m_dist, dist);
        }
    }

    float cellPixel = (1.0 / pointCellWidth);
    color += 1.0 - smoothstep(pointRadiusOfCell, pointRadiusOfCell + cellPixel, m_dist);

    fragColor = vec4(vec3(color), 1.0);
}
