//Dianna Cornett's checkerboard assignment, base version

//modifiable variables for number of rows and columns
float columns = 6.0;
 float rows = 4.0;


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //setting up screen 
	vec2 uv = fragCoord.xy / iResolution.xy;


    //loops that form boxes in an alternating pattern
    if(int(uv.x*columns)%2 == 0)
    {

        if(int(uv.y*rows)%2 ==0)
        {
            fragColor = vec4(0.0, 0.0, 0.0, 1.0);
        }
        else
        {
             fragColor = vec4(1.0, 1.0, 1.0, 1.0);
        }

    }
    else
    {

        fragColor = vec4(0.0, 0.0, 0.0, 0.0);
        if(int(uv.y*rows)%2 ==0)
        {
            fragColor = vec4(1.0, 1.0, 1.0, 1.0);
        }
    }
}
