#define PI 3.1415926535897932384626433832795
float scale = 0.7; // How far you zoom in
vec2 center = vec2(0.0, 0.0); //Which point on the complex plane the screen is centered on.

vec3 huetorgb(float h)
{
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(vec3(h) + K.xyz) * 6.0 - K.www);
    return clamp(p - K.xxx, 0.0, 1.0);
}

void iterate(inout vec2 z, vec2 c) {
	vec2 tz = vec2(cos(1.0*PI*z.x), sin(1.0*PI*z.x));
    tz = exp(-1.0*PI*z.y)*tz;
    vec2 mul = vec2(1.0, 0.0); //Changing this value does cool/weird stuff
    tz = mat2(tz,-tz.y,tz.x) * mul;
    vec2 add = vec2(iTime/10.0, 0.0); //Changing this value does cool/weird stuff
    z=tz+add;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {	

	vec2 uv = (2.0*fragCoord - iResolution.xy) / iResolution.y;
    uv *= scale;
    uv += center;

    vec2 z = uv;
    vec2 c = uv;

    float iterations = -1.0;;
    for (float i = 0.0; i < 128.0; i++) {
        iterate(z,c);
        if (dot(z,z) > 1e7) {
            iterations = i;
            break;
        }
    }

    if (iterations >= 0.0) {
    	float hue = pow(iterations/50.0,0.15);
    	hue += 0.5;

		fragColor = vec4(vec3(huetorgb(hue)) ,1.0);
    }
    else fragColor = vec4(0.0);
}
