void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 xy = fragCoord.xy / iResolution.xy; // resolution crap
    vec4 texVid = texture(iChannel0,xy); // Grab the texture
    vec4 texBac = texture(iChannel1,xy); // Grab the other texture
    if( (texVid.r + texVid.b) < texVid.g){ // See if pixel is green
         texVid = texBac; // Replace pixel with one from iChannel1
    }

    fragColor = texVid; //Display the texture
}
