float pi = 3.14159265359;
float bbsm = 1739.;

vec2 bbsopt(in vec2 a) {
	return fract(a*a*(1./bbsm))*bbsm;
}

vec2 mod1024(in vec2 a) {
	return fract(a*(1./1024.))*1024.;	
}

float rand(vec2 co) {
    return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453);
}

vec4 hash(in vec2 pos) {
	vec2 a0 = mod1024(pos*pi);
	vec2 a1 = bbsopt(a0);
	vec2 a2 = a1.yx + bbsopt(a1);
	vec2 a3 = a2.yx + bbsopt(a2);
	return fract((a2.xyxy + a3.xxyy + a1.xyyx)*(1./bbsm));
}

vec3 rgb2hsv(vec3 c) {
    vec4 K = vec4(0.0, -1.0 / 3.0, 2.0 / 3.0, -1.0);
    vec4 p = mix(vec4(c.bg, K.wz), vec4(c.gb, K.xy), step(c.b, c.g));
    vec4 q = mix(vec4(p.xyw, c.r), vec4(c.r, p.yzx), step(p.x, c.r));

    float d = q.x - min(q.w, q.y);
    float e = 1.0e-10;
    return vec3(abs(q.z + (q.w - q.y) / (6.0 * d + e)), d / (q.x + e), q.x);
}

vec3 hsv2rgb(vec3 c)
{
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

vec4 randomColor(in vec2 fragCoord) {
    const vec2 scale = vec2(100., 100.);
	const int num = 1; //average multiple instances

	vec2 p = scale*floor(fragCoord.xy);

	vec4 c = vec4(0.);
	for(int i=0; i<num; i++)
		c+= hash(p+float(i*1024));
	c*=1./float(num);

	return vec4(c.x,c.y,c.z,c.w);
}

vec3 randomRGB(in vec2 fragCoord) {
	float s = rand(fragCoord) * 3.0;
    if (s < 1.0) {
        return vec3(1.0, 0.0, 0.0);
    } else if (s < 2.0) { 
        return vec3(0.0, 1.0, 0.0);
    } else {
        return vec3(0.0, 0.0, 1.0);
    }

}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec4 original = texture(iChannel0, fragCoord/iResolution.xy);

    if(iFrame==0) {
        vec3 color = randomRGB(fragCoord);
        fragColor = vec4(color.r, color.g, color.b, 1.0);
    } else {

    	vec4 l = texture(iChannel0,(fragCoord+vec2(-1.0, 0.0))/iResolution.xy);
		vec4 tl = texture(iChannel0,(fragCoord+vec2(-1.0, 1.0))/iResolution.xy); 
	    vec4 t = texture(iChannel0,(fragCoord+vec2(0.0,-1.0))/iResolution.xy); 
    	vec4 tr = texture(iChannel0,(fragCoord+vec2(1.0, 1.0))/iResolution.xy);
	    vec4 r = texture(iChannel0,(fragCoord+vec2(1.0, 0.0))/iResolution.xy); 
	    vec4 br = texture(iChannel0,(fragCoord+vec2(1.0, -1.0))/iResolution.xy);
	    vec4 b = texture(iChannel0,(fragCoord+vec2(0.0, 1.0))/iResolution.xy); 
        vec4 bl = texture(iChannel0,(fragCoord+vec2(-1.0,-1.0))/iResolution.xy);

		float red = (l.r + tl.r + t.r + tr.r + r.r + br.r + b.r + bl.r) / 8.0;
		float green = (l.g + tl.g + t.g + tr.g + r.g + br.g + b.g + bl.g) / 8.0;
		float blue = (l.b + tl.b + t.b + tr.b + r.b + br.b + b.b + bl.b) / 8.0;

        vec4 avg = vec4(red, green, blue, 1.0);
        vec3 hsv = rgb2hsv(avg.rgb);

        hsv.b = 0.5;
        hsv.g = 1.0;
        hsv = hsv2rgb(hsv);
        fragColor = vec4(hsv.r, hsv.g, hsv.b, 1.0);


    }
}
