renderpass.code:
float plot_edge(float d) {
	return smoothstep(3.0 / iResolution.y, 0.0, abs(d));
}

float plot_solid(float d) {
	return smoothstep(3.0 / iResolution.y, 0.0, max(0.0, d));
}

float plot_solid_with_blooming(float d) {
	return 1.0 - clamp(d, 0.0, 1.0);
}

float sphere(vec2 p) {
	return length(p) - 0.5;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
    float d = sphere(uv);
    vec3 c0 = vec3(0.3, 0.8, 1.0); 		// sky
    vec3 c1 = vec3(1.0, 0.8, 0.6); 		// blooming color
    vec3 c2 = vec3(1.0, 0.0, 0.0); 		// red sun
    vec3 bg = mix(c0, c1, plot_solid_with_blooming(d));
    fragColor = vec4(mix(bg, c2, plot_solid(d)), 1.0);
}
