void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = fragCoord.xy / iResolution.y ;
    vec2 lightPos = p - vec2(0.9,0.5);
    vec4 col = vec4(1.,0.,0.,1.);

	vec4 tex = texture(iChannel0, lightPos+vec2(0.5,0.5));

    col *= smoothstep(0.4, 0.03, length(lightPos));

	fragColor = tex+col;
}
