vec3 houseCol = vec3(0.8, 0.4, 0.6);
vec3 chimCol = vec3(1.0, 0.0, 0.4);
vec3 windowCol = vec3(0.0, 0.0, 1.0);
vec3 windowCol2 = vec3(0.0, 0.0, 1.0);
vec3 bgColor = vec3(0.2, 0.2, 0.2);
vec2 houseLocation = vec2(0.3, 0.0);
vec2 chimLocation = vec2(0.7, 0.0);
vec2 windowLocation1 = vec2(0.32, 0.15);
vec2 windowLocation2 = vec2(0.55, 0.15);
vec2 houseSize = vec2(0.4, 0.3);
vec2 chimSize = vec2(0.103, 0.4);
vec2 windowSize = vec2(0.1, 0.1);
bool houseSwitch = true;
bool chimSwitch = true;
bool windowSwitch = true;

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    uv.y *= iResolution.y/iResolution.x;

    //creates multiple layers to be filled and then blended together    
    vec4 layer1 = vec4(bgColor, 0.2);
    vec4 layer2;
    vec4 layer3;
    vec4 layer4;
    vec4 layer5;

    //creates the body of the house
    float interpX = step(houseLocation.x, uv.x) - step(houseLocation.x + houseSize.x, uv.x);
    float interpY = step(houseLocation.y, uv.y) - step(houseLocation.y + houseSize.y, uv.y);
    layer2 = vec4(houseCol, (interpX * interpY));  

    //creates the chimney
    float interpX2 = step(chimLocation.x, uv.x) - step(chimLocation.x + chimSize.x, uv.x);
    float interpY2 = step(chimLocation.y, uv.y) - step(chimLocation.y + chimSize.y, uv.y);
    layer3 = vec4(chimCol, (interpX2 * interpY2)); 

    //creates a window
    float interpX3 = step(windowLocation1.x, uv.x) - step(windowLocation1.x + windowSize.x, uv.x);
    float interpY3 = step(windowLocation1.y, uv.y) - step(windowLocation1.y + windowSize.y, uv.y);
    layer4 = vec4(windowCol, (interpX3 * interpY3));   

    //creates a second window
    float interpX4 = step(windowLocation2.x, uv.x) - step(windowLocation2.x + windowSize.x, uv.x);
    float interpY4 = step(windowLocation2.y, uv.y) - step(windowLocation2.y + windowSize.y, uv.y);
    layer5 = vec4(windowCol, (interpX4 * interpY4));   


    vec4 blend = layer1;

    //blends the layers and each layer has a true or false switch
    if(houseSwitch){
    	blend = mix(blend, layer2, layer2.a);
    }
    if(chimSwitch){
    	blend = mix(blend, layer3, layer3.a);
    }
    if(windowSwitch){
    	blend = mix(blend, layer4, layer4.a);
        blend = mix(blend, layer5, layer5.a);
    }

    fragColor = blend;
}
