#define PI 3.1415926535897932384626433832795



// Number of mirror segments

#define N 16.0


// Speed of rotation

#define S (PI/16.0)


// ZOOM

#define Z 1.5




void mainImage( out vec4 fragColor, in vec2 fragCoord )
{


    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 cc = uv - vec2(0.5,0.5);
    vec2 po = vec2( atan(cc.x,cc.y) , length(cc) );

    float fft1=(texture(iChannel1,vec2(0.1,0.25)).r    
               +texture(iChannel1,vec2(0.5,0.25)).r
               +texture(iChannel1,vec2(0.8,0.25)).r)*8.0;

    float fft2=(texture(iChannel1,vec2(0.6,0.25)).r    
               +texture(iChannel1,vec2(0.7,0.25)).r
               +texture(iChannel1,vec2(0.8,0.25)).r)*1.0;

    float s=1.0;
    float n=1.0+ceil(fft1);
    float z=fft2;

	float f=(2.0*PI/n);

    float m=mod((2.0*PI)+po.x,f);

    if(m>f/2.0){ m=f-m; }

    po.x = m+(iTime*s);
    po.y /=z;

    cc= vec2(cos(po.x),sin(po.x))*po.y;


    // Output to screen
    fragColor = texture( iChannel0, cc+vec2(0.5,0.5) ).rgba;

    fragColor=fragColor*8.0-4.0; // contrast

}
