void mainImage( out vec4 f, in vec2 g )
{
	vec2 si = iResolution.xy;

	vec2 h = (g+g-si.xy)/si.y * 0.6;

	vec4 tex = texture(iChannel0, g/si).rrrr;

	g /= 20.;

	float r = cos(h.x)*cos(h.y)*.7;

	vec4 col = vec4(.34,1,1,1)/25.;

	float hash = sin( 654654.6465 * sin( 1e3 * length(floor(g))));

	float branch = g.y + g.x * sign(  hash );

	float rep = cos(3.14 * branch);

	float t = sin((iTime + 25.) * 0.2)*.5 +.5;

    f = (r - col / rep) * 0.9 + (r - col / step(tex, vec4(rep + t*2.16))) * 0.1;
}
