precision highp float;
uniform float time;
uniform vec2 resolution;
varying vec3 fPosition;
varying vec3 fNormal;
vec3 rim(vec3 color, float start, float end, float coef)
{
  vec3 normal = normalize(fNormal);
  vec3 eye = normalize(-fPosition.xyz);
  float rim = smoothstep(start, end, 1.0 - dot(normal, eye));
  return clamp(rim, 0.0, 1.0) * coef * color;
}
vec2 blinnPhongDir(vec3 lightDir, float lightInt, float Ka, float Kd, float Ks, float shininess)
{
  vec3 s = normalize(lightDir);
  vec3 v = normalize(-fPosition);
  vec3 n = normalize(fNormal);
  vec3 h = normalize(v+s);
  float diffuse = Ka + Kd * lightInt * max(0.0, dot(n, s));
  float specular =  Ks * pow(max(0.0, dot(n,h)), shininess);
  return vec2(diffuse, specular);
}
vec3 luma = vec3(0.299, 0.587, 0.114);
vec3 colorNormal(vec3 col1, vec3 col2, vec3 col3)
{
  vec3 n = normalize(fNormal);
  return clamp(col1*n.x + col2*n.y + col3*n.z,
              vec3(0.0), vec3(1.0));
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec4 fragColorPart = vec4(fNormal, 1.0);
  vec4 light = vec4(-10.0,-16.0,1.0,0.0);
  vec3 newcolx = rim(vec3(blinnPhongDir(colorNormal(luma,luma,luma), 2.0, 0.4,0.9,0.6,125.2),0.3), 0.0,0.0,1.0);
  vec4 fragColorseg = vec4(newcolx, 1);
  vec4 newcoli = mix(fragColorPart, fragColorseg, 2.0);
  fragColor = newcoli;
}
