 // give a letter following its array coordinate in the texture
vec4 char(vec2 pos, vec2 letterPos, vec2 origin, vec2 size) {
    pos = (pos-origin)/size + letterPos/16.;
    pos = clamp(pos,letterPos/16.,(letterPos+1.)/16.);
	return texture( iChannel0, pos);
}

vec4 fontUnion(vec4 f1, vec4 f2){
    if(f1.w<f2.w)
        return f1;
    else
        return f2;
}

// you can change the size of the font by changing vec2(3.0,3.0)
#define LETTER(x,y) font = fontUnion(font,char(uv,vec2(x,y),pos,vec2(3.0,3.0))); pos+=vec2(.1,0.);

#define A LETTER(1.,11.)
#define B LETTER(2.,11.)
#define C LETTER(3.,11.)
