#define n pos = vec2(.01*sin(pos.y*iTime),pos.y-.15);
#define _ pos += vec2(.1,0.);


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

	vec4 font = vec4(1.);
    vec2 pos = vec2(.02*sin(iTime),0.7+.03*cos(iTime));

// ====== use macro to make it as if you were typing =====
    H E L L O n
	W O R L D n n
	I _ M n
    Y A K O U D B Z

// ======

    vec4 fontColor = vec4(1.0,0.1,0.3,1.);

    vec3 lightDir = normalize(vec3(0.5,0.5,-0.5));
    vec3 normal = normalize(vec3(-font.g, font.b, 0.0));
    vec3 reflectDir = reflect(-lightDir, normal);

    vec3 viewDir = normalize(vec3(0.,0.,1.0) - vec3(uv,font.w));
	float spec = pow(max(dot(viewDir, reflectDir),0.0), 24.);

    float light = .5 + 1.*max(dot(normal,lightDir),0.0) + 2.*spec;

    fragColor = 5.*light*(.51-font.w)*fontColor;
}
