#define THRESHOLD 0.125
#define HISTERESIS 0.05



float gray(vec4 col)
{
     return dot(col, vec4(0.299, 0.587, 0.114, 0.0));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec3 texel0 = vec3(1.0 / iChannelResolution[0].x, 0.0, 0.0);
    vec4 one = vec4(1.0);

    vec4 col = texture(iChannel0, uv);

    float d0 = dot(col, one);
    float d1 = dot(texture(iChannel0, uv + texel0.xy), one);
    float d2 = dot(texture(iChannel0, uv + texel0.yx), one);
    float d = (abs(d1 - d0) + abs(d2 - d0)) * 0.5;

/*    
    float d0 = gray(col);
    float d1 = gray(texture(iChannel0, uv + texel0.xy));
    float d2 = gray(texture(iChannel0, uv + texel0.yx));
    float d = abs(d1 - d0) + abs(d2 - d0);
*/    

    float t = (sin(iTime) + 1.0) * 0.5 * THRESHOLD;

    d = 1.0 - smoothstep(t, t + HISTERESIS, d);
	fragColor = col + vec4(d - t);
//	fragColor = col + d;
}
