void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec3 video1 = vec3(texture(iChannel0, uv));
    vec3 video2 = vec3(texture(iChannel2, uv));

    vec3 img = vec3(uv.x, uv.y, 0.1);

    video2 = sin(sqrt(iTime) * (mod(video1, video2) * 0.3));

    if( video2.g > 0.01 ) video2.g = smoothstep(video2.g, 0.01, 0.85);
    if( video2.r > 0.02 ) mod(video2.r, sin(iTime));
    if( video2.b > 0.03 ) mod(video2.b, cos(iTime));

    img = video2;

	fragColor = vec4(img, 1.0);
}
