float cau = sqrt(2.), civ = 250.;
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = fragCoord.xy / iResolution.xy;
    vec3 c = vec3(0);
    vec2 alpha = p*2.-1.;
    float beta = length(alpha/2.);
    beta=pow(beta,3.);
    beta*=8.23;
    beta/=cau;
    vec3 af = vec3(0);
    float ci = (1./civ)*beta;
    af.x=texture(iChannel0,p+vec2(-1./cau)*ci).r;
    af.y=texture(iChannel0,p+vec2(-1.,1.)/cau*ci).g;
    af.z=texture(iChannel0,p+vec2(1.,0)*ci).b;
    c = af;
    fragColor = c.rgbr;
}
