float metaball(vec2 p, float r)
{
    return 1.0/((p.x * p.x) + p.y * p.y) * r;
}

vec3 hsv(float h, float s, float v)
{
  return mix( vec3( 1.0 ), clamp( ( abs( fract(
    h + vec3( 3.0, 2.0, 1.0 ) / 3.0 ) * 6.0 - 3.0 ) - 1.0 ), 0.0, 1.0 ), s ) * v;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 ndc_xy = -1.0 + 2.0 * fragCoord.xy / iResolution.xy;
    float aspect_ratio = iResolution.x / iResolution.y;  
    vec2 scaled_xy = ndc_xy * vec2(aspect_ratio, 1.0);

	vec2 uv = scaled_xy / 2.0 * sin(fragCoord.y) * cos(fragCoord.x);

    float d 
        = metaball(vec2(uv.x * sin(iTime), uv.y), 0.1)
        + metaball(vec2(uv.x, uv.y * sin(iTime)), 0.1);

    vec3 color = hsv(length(uv) * sin(iTime * 0.01), 0.5, 1.0);
    if(d > 0.707) color = color - 0.4083;
    fragColor = vec4(color, 1.0);    
}
