void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    if (uv.y > 0.5) {
        //First row shows FFT analysis (amplitude as a function of frequency)
        vec4 orange = vec4(1.0, 0.5, 0.0, 1.0);
		fragColor = texture(iChannel0, vec2(uv.x, 0.25)).x * orange;
    } else {
        //Second row shows waveform (amplitude as a function of time)
        vec4 purple = vec4(0.5, 0.0, 1.0, 1.0);
        fragColor = texture(iChannel0, vec2(uv.x, 0.75)) * purple;
    }
}
