	vec2 si = iResolution.xy;

	vec2 uv = (g+g-si)/min(si.x,si.y) * 1.2;

	vec2 v = vec2(490., 150. + 15. * sin(iTime*0.25));

	vec2 z =  vec2(0);

	vec2 c = (v*2.-vec2(900,300))/150.;

	float it = 0.;
	float d = 0.;
    vec2 lz = z;
	for (int i=0;i<100;i++)
	{
        lz = z;
		if (dot(z,z) > 4.) break;
		z = zpow(z, 6. * (sin(iTime*.25-0.2)*.5+.5)) + uv;
		it++;
	}

    z = mix(lz,z,abs(log2(log2(float(it)/(25. * sin(0.25)))))+1e4);

