void mainImage(out vec4 color, in vec2 pixCoords )
{


    float time = iTime * 5.0;
    float moveTime = iTime;

    float radius = 200.0 + abs(cos(time) * 50.0);
    float middleRadius = 150.0 + abs(cos(time) * 50.0);
    float smallRadius = 100.0 + abs(cos(time) * 50.0);

    float middleX = (iResolution.x / 2.0) + cos(moveTime) * 50.0;
    float middleY = (iResolution.y / 2.0) + cos(moveTime) * 50.0;

    float length = sqrt(pow(pixCoords.x - middleX,2.0) + pow(pixCoords.y - middleY, 2.0));


    if ( length < smallRadius ) 
    {
        color = vec4(0.0, 0.0, 0.8, 1.0);
    }
    else if (length < middleRadius)
    {
        color = vec4(0.0, 0.2, 1.0, 1.0);
    }
    else if (length < radius)
    {
        color = vec4(0.0,0.6,1.0,1.0);
    }

}
