void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(texture( iChannel0, fragCoord/iResolution.xy).x);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 u = fragCoord/iResolution.xy;
    float l = fract(sin(dot(u,vec2(123.,987.))+iTime) * 9879.);
    float a = texture(iChannel0,fragCoord/iResolution.xy).x;

    if(iFrame == 0){a = float(l>.999);}

    float t;

    t = texture(iChannel0,(fragCoord+vec2(3.,0.))/iResolution.xy).x;
    if(t != 0. && l > .9){a += 1.;}

    t = texture(iChannel0,(fragCoord+vec2(0.,3.))/iResolution.xy).x;
    if(t != 0. && l > .9){a -= 1.;}

    t = texture(iChannel0,(fragCoord+vec2(-3.,0.))/iResolution.xy).x;
    if(t != 0. && l > .9){a += 1.;}

    t = texture(iChannel0,(fragCoord+vec2(0.,-3.))/iResolution.xy).x;
    if(t != 0. && l > .9){a -= 1.;}
    //////////////////////////////////////
    t = texture(iChannel0,(fragCoord+vec2(1.,1.))/iResolution.xy).x;
    if(t != 0. && l < .9){a -= 1.;}

    t = texture(iChannel0,(fragCoord+vec2(1.,-1.))/iResolution.xy).x;
    if(t != 0. && l < .9){a += 1.;}

    t = texture(iChannel0,(fragCoord+vec2(-1.,1.))/iResolution.xy).x;
    if(t != 0. && l < .9){a -= 1.;}

    t = texture(iChannel0,(fragCoord+vec2(1.,-1.))/iResolution.xy).x;
    if(t != 0. && l < .9){a += 1.;}

    fragColor = vec4(a);
}
