void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    uint s = 0x12345678u; // "random" start state
    //uint s = 0x00008000u; // single cell start state

    //float sc = 1.;                 // looping 32px
    //float sc = iResolution.x / 32.; // scaled
    float sc = iResolution.x / 128.; // scaled

    for(int i=0;i<int(fragCoord.y/sc);++i)
    {
        // shift-rotate
        uint a = (s << 1) | (s >> 31);
        uint b = (s);
        uint c = (s >> 1) | (s << 31);

        //s = (some expression)         // rule:

        //s = 0u;                       // 0
        //s = ~(a|b|c);                 // 1
        //s = (a^c)&~b;                 // 18
        s = a^(b|c);                  // 30
    }

    // shift to output
    fragColor = vec4((s>>int(fragCoord.x/sc)%32)&1u);
}
