#define EPSILON pow(10., -4.)
#define bigness 0.01
#define PI 3.1415926
#define light_col vec3(1., 0., 0.)
#define light_bigness 0.015
vec3 tex (vec3 p, sampler2D t){
    vec3 t1 = texture(t, p.xy).rgb/3.;
    t1+=texture(t, p.xz).rgb/3.;
    t1+=texture(t, p.yz).rgb/3.;
    return t1;
}
float align (float diff, float dim){
    if(diff>=dim){
        return diff-dim;
    } else {
        return 0.;
    }
}
float box3D (vec3 boxPos, vec3 boxSize, vec3 pos){
    float n = align(abs(pos.x-boxPos.x), boxSize.x)+align(abs(pos.y-boxPos.y), boxSize.y);
    n+=align(abs(pos.z-boxPos.z), boxSize.z);//just to keep the lines short
    return n;
}
float sphere (vec3 spherePos, vec3 pos, float radius){
    return length(pos-spherePos)-radius;
}
//Credit to Inigo Quilez for original function-- https://iquilezles.org/articles/distfunctions
float CappedCylinder( vec3 pos, vec3 p, vec2 h )
{
  p = p-pos;
  vec2 d = abs(vec2(length(p.xz),p.y)) - h;
  return min(max(d.x,d.y),0.0) + length(max(d,0.0));
}
float cylinder (vec3 pos, vec3 p, float radius, float height){
    vec3 diff = p-pos;
    float yd = abs(diff.y);
    return (length(diff.xz)-radius)+smoothstep(yd, -height*0.5, height*0.6);//+smoothstep(abs(diff.y)-height, 0., -height);
}
float doubleCone (vec3 pos, vec3 p, vec2 s){
    vec3 diff = p-pos;
    float yd = abs(diff.y)-s.y;
    return (length(diff.xz)-s.x)-yd;
}
float XZplane (float y, vec3 p){
    return p.y-y;
}
//useful functions
vec2 rotate2D (vec2 p, float angle){
    float a = atan(p.y, p.x)+angle;
    float m = length(p);
    return vec2(cos(a)*m, sin(a)*m);
}
//from IQ -- https://iquilezles.org/articles/smin
float smin( float a, float b, float k)
{
    float h = clamp( 0.5+0.5*(b-a)/k, 0.0, 1.0 );
    return mix( b, a, h ) - k*h*(1.0-h);
}
float mymin (float a, float b, float k){
    return min(a, b)-abs(a-b)/(k*100.);
}
vec3 calcLight (float t) {
    return vec3(cos(t), sin(t), sin(t*2.))*bigness*2.;
}
vec3 calcDefaultLight () {
    return calcLight(iTime*0.5)*2.;
}
vec4 SDF (vec3 p){
    vec3 matCol = vec3(0.);
    float total = 0.;
    float ptotal = total;
   	total = XZplane(-0.1, p);
    matCol = tex(p, iChannel1);
    ptotal = total;
    total = min(total, sphere(vec3(0.), p, 0.01));
    total = min(total, sphere(calcLight(iTime)*3., p, 0.02));
    if (total!=ptotal){
        matCol = vec3(0., 0.7, 0.);
    }
    ptotal = total;
    //the light (red)
    total = min(total, sphere(calcDefaultLight(), p, light_bigness));
    if(total!=ptotal){
        matCol = vec3(1., 0., 0.);
    }
    return vec4(total, matCol);
}
vec4 trace (vec3 o, vec3 r) {
    float t = 0.;
    vec3 p = o;
    for(int i = 0; i<72; i++){
        p = o+r*t;
        float d = SDF(p).x;
        t+=d*0.7;
    }
    return vec4(p, t);
}
//Credit to Jamie Wong http://jamie-wong.com/2016/07/15/ray-marching-signed-distance-functions/#surface-normals-and-lighting
vec3 estimateNormal(vec3 p) {
    return normalize(vec3(
        SDF(vec3(p.x + EPSILON, p.y, p.z)).x - SDF(vec3(p.x - EPSILON, p.y, p.z)).x,
        SDF(vec3(p.x, p.y + EPSILON, p.z)).x - SDF(vec3(p.x, p.y - EPSILON, p.z)).x,
        SDF(vec3(p.x, p.y, p.z  + EPSILON)).x - SDF(vec3(p.x, p.y, p.z - EPSILON)).x
    ));
}

//made this one myself with some tutorials I found out there on the 
float diffuseLight (vec3 n, vec3 r){
    return dot(n, -r);
}
float specularLight (vec3 n, vec3 r){
    vec3 nr = reflect(r, n);
    return pow(max(dot(nr, -r), 0.), 4.);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    //setup coordinates
    vec2 mouse = iMouse.xy/iResolution.xy - vec2(0.5, 0.);
    if(iMouse.xy == vec2(0.))mouse = vec2(0.5, 0.2);
    mouse.x*=5.;
    mouse.y*=1.;
    vec2 uv = fragCoord/iResolution.xy;
	uv-=0.5;
    uv.x*=iResolution.x/iResolution.y;

    //setup camera + rays
    float angle = mouse.x;
    float fov = 0.5;
    float camDist = 1.;
    vec3 camera = vec3(0., 1., 0.)*camDist;
    vec3 ray = vec3(uv.x, -1., uv.y);
    ray = normalize(ray);
    mouse.y -= 0.2;
    mouse.y *= -3.;
    camera.xy = rotate2D(camera.xy, mouse.x);
    camera.yz = rotate2D(camera.yz, mouse.y);
    ray.xy = rotate2D(ray.xy, mouse.x);
    ray.yz = rotate2D(ray.yz, mouse.y);


    //do the raymarching
    vec4 v = trace(camera, ray);
    vec3 normal = estimateNormal(v.xyz);//keep here
    vec3 col = vec3(0.);
    col = SDF(v.xyz).yzw;//material color
    //col = tex(v.xyz, iChannel1);

    //do the lighting https://learnopengl.com/Lighting/Basic-Lighting
    vec3 ligr = -normalize(v.xyz-calcDefaultLight());
    col+=texture(iChannel0, reflect(ray, normal)).r*0.3;//reflection
    col+=diffuseLight(normal, ray)*0.5;
    //col+=diffuseLight(normal, ray)*0.5;

    //do shadows (march from ending spot to light)
    float endSDF = SDF(v.xyz).x;
    vec3 ligna = calcDefaultLight();
    vec3 goray = normalize(ligna-v.xyz);
    float tt = 0.;
    bool hitSomething = false;
    vec3 sp = vec3(0.);
    float s = 0.;
    float dil = 0.;
    for(int i = 0; i<128; i++){
        sp = v.xyz+goray*tt;
        s = SDF(sp).x;
        tt+=s*0.8;
        dil = length(sp-ligna);
        if(s<endSDF&&dil>light_bigness*1.1){//if run into something other than light, break
            break;
        }
    }
    if(s<endSDF&&dil>light_bigness*1.1){//if is touching something (not the light) then it's behind it (hence, shadow)
        col*=0.3;
    }

    col *= 1./(1.+pow(v.w*2., 2.)*0.01);//distance shading
    // Output to screen
    fragColor = vec4(col,1.0);
}
