void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    highp float X = fragCoord.x;
    highp float Y = fragCoord.y;
    highp float sloTime = iTime/3.;
    highp int color = int(iTime * 99. + float(int(sin(sloTime)*X + cos(sloTime)*Y) & int(- cos(sloTime)*X + sin(sloTime)*Y)))%610;

    // Output to screen
    fragColor = vec4(1. - float(color)/100., float(color)/610., float(color)/610., 1);
}
