void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = fragCoord / iResolution.xy;

    float x_offset = sin(iTime) * sin(uv.y * 12.0) / 10.0;
    float y_offset = cos(iTime / 4.0) * sin(uv.x * 12.0) / 20.0;
    //float y_offset = 0.0;

    uv = vec2(uv.x + x_offset, uv.y + y_offset);
    vec4 col = texture(iChannel0, uv);
    fragColor = col;
}
