void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //screen uv coord
	vec2 R = iResolution.xy,
        uv = (fragCoord - .5*R) / iResolution.y;

    //setup simple camera ray
    vec3 rp = vec3(iTime*0.14,iMouse.y/50.+iTime*0.091,mod(iTime,100.)+iMouse.x/50.);
    vec3 rd = normalize(vec3(uv,1.));

    //color accumulation and distance ray travelled
    vec3 c = vec3(0.);
    float s = 0.;

    for (int i = 0; i < 24; i++) {
        vec3 hp = rp+rd*s;

        float a = (.09+0.02*rand(hp*.09127));

        //color sample at point
        float cc = a*mix(10.,0.,float(i)/128.);
        c += texture(iChannel0,(hp.xy+hp.zz*sin((hp.yx+hp.z*3.)*0.25)*.035)*0.15).xyz*cc*0.1;

        //step ray forward
        s += a;
    }

    fragColor = vec4(pow(c,vec3(4.)),1.);
}
