float rand(vec2 co) {
    return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    float sinModulator = sin(fragCoord.x / sin(iTime)*0.05)*sin(iTime)*3.0;
    float sinMain = sin(sin(iTime*1.3)*10.0 + iTime + fragCoord.x/(sin(iTime)*20.0+40.0))/(6.0+sinModulator); //sinModulator;
    float yCoord = 0.5+sinMain;

    if (uv.y < yCoord-0.02 || uv.y > yCoord+0.02) {
        fragColor.rgb = vec3(0.0); 
    } else {
        fragColor.rgb = vec3(0.78, 0.21, 0.7);
    }

    if (uv.y < yCoord-0.02) {
        vec2 seed = vec2(2.0, 8.0);
        fragColor.rgb = vec3(rand(seed), 0.0, 0.0);
    }

    vec2 square = vec2(8.0, 8.0);
}
