vec4 draw_crosshair(vec2 UV, vec2 SIZE)
{
    vec2  uv = UV -= 0.5;

    float stroke_size = 0.015;
    float inner_clip_size = 0.25;
    float outer_clip_size = 0.75;

	float cross_x = (step(-stroke_size, uv.y)) * (1.0 - step(stroke_size, uv.y));
    float cross_y = (step(-stroke_size, uv.x)) * (1.0 - step(stroke_size, uv.x));
	float cross_final = cross_x + cross_y;

    float inner_clip_x = (step(-inner_clip_size, uv.y)) * (1.0 - step(inner_clip_size, uv.y));
    float inner_clip_y = (step(-inner_clip_size, uv.x)) * (1.0 - step(inner_clip_size, uv.x));
    float inner_clip = inner_clip_x * inner_clip_y;

    float outer_clip_x = (step(-outer_clip_size, uv.y)) * (1.0 - step(outer_clip_size, uv.y));
    float outer_clip_y = (step(-outer_clip_size, uv.x)) * (1.0 - step(outer_clip_size, uv.x));
    float outer_clip = outer_clip_x * outer_clip_y;

    float clip = outer_clip - inner_clip;

    float crosshair = cross_final * clip;

    return vec4(crosshair);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 size 	= iResolution.xy;
    vec2 uv 	= fragCoord.xy / iResolution.xy;

    fragColor = draw_crosshair(uv, size);
}
