void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec3 col = vec3(min(1.0, sin(iTime-0.5)*uv.y*3.0), min(1.0, -sin(iTime)*uv.y*3.0), min(1.0, (cos(iTime)+sin(iTime))*uv.x*uv.y)); 

    // Time varying pixel color
    if ((1.0-sin(uv.x*40000.0+iTime) < uv.y)){
    	col = vec3(0);
    }
    //vec3 col = uv.xyx * vec3(pow(1.5, 3.0 + 2.5*cos(iTime)));

        //0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    // Output to screen
    fragColor = vec4(col,1.0);
}
