// Really cheap color visualizer 
//   v1.0  colorized variation of the https://www.shadertoy.com/view/MlV3DW visualizer
//   v1.1  colored again

#define usemic false // for some reason the mic input does not work the same as soundcloud or the basic sound input
#define n 7.         // number of rays
#define T(v) 0.8*texture(iChannel0,abs((v)-fract(p))) 

void mainImage( out vec4 o, in vec2 u )
{
    float time = iTime*0.05;
    vec2 R = iResolution.xy;
    u = (u + u - R) / R.y;
    float t = cos(time+length(u)*20.*sin(time*14.))/10.;
    u *= mat2(cos(t),sin(t),-sin(t),cos(t));
    vec2 p = vec2(exp2(length(u))-16.0*time,0.5*n*atan(u.y,u.x)/acos(-1.));
    o.r = T(usemic ? 0.98 : 0.3).r; 
    o.g = T(usemic ? 0.79 : 0.5).r; 
    o.b = T(usemic ? 0.86 : 0.7).r; 
}
