void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.y*2.0-iResolution.xy/iResolution.y;

    float l = 0.0;
    vec2 rot = vec2(sin(iTime*0.1),cos(iTime*0.1));

    for(int i = 0; i < 256; i++) {

        uv = 1.1*(uv.x*vec2(rot.x,rot.y)+uv.y*vec2(-rot.y,rot.x));

        if(pow(0.05*uv.x, sin(uv.y)) > 12.0) {
            break;
        }
        l++;
    }

    fragColor = vec4(mod(l, 2.0));
}
