void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float t = (atan(tan(iTime/3.0-3.1415/2.0))+1.5708);
	float n = pow(pow(pow(2.0,t),t), t*0.6);

    vec2 uv = fragCoord.xy / iResolution.xy;
    uv = uv * 2.0 - 1.0;
    uv *= iResolution.xy / iResolution.yy;
    vec3 color = vec3(1.0,1.0,1.0);
    float i = 0.0;
    for (int q = 0; q < 129; q++) {
        if (i < n) {
        	vec3 colorTmp = color;
            vec2 center = vec2(sin(i/n*2.0*3.1415926)/1.0, cos(i/n*2.0*3.1415926)/5.0);
            vec2 d = center-uv;
            if (d.x*d.x+d.y*d.y < 0.5) {
                colorTmp.x += 1.0;
                if (colorTmp.x > 1.5) {
                    colorTmp.x = 0.0;
                    colorTmp.y += 1.0;
                }
                if (colorTmp.y > 1.5) {
                    colorTmp.y = 0.0;
                    colorTmp.z += 1.0;
                }
                if (colorTmp.z > 1.5) {
                    colorTmp.x = 0.0;
                    colorTmp.y = 0.0;
                    colorTmp.z = 0.0;
                }
                if (i+1.0 < n) {
                	color = colorTmp;
                } else {
                    float ratio = n-i;
                    color = colorTmp*(ratio) + color*(1.0-ratio);
                }
            } else if (d.x*d.x+d.y*d.y < 0.51) {
             	color = vec3(0.0,0.0,0.0);
                i=n;
            }
            i+=1.0;
        }
    }
	fragColor = vec4(color,1.0);
}
