float rand(vec2 n) { 
    return fract(sin(dot(n, vec2(12.9898, 4.1414))) * 43758.5453);
}

float line(float p, float x, float glow) {
    return 1. - pow(abs(p - x), glow);
}

float grid(vec2 uv) {
    float glow = 0.05;
    float c = line(uv.x, 0.25, glow) + line(uv.x, 0.75, glow)
            + line(uv.x, 0.5, glow)
        	+ line(uv.x, 0.0, glow) + line(uv.x, 1.0, glow)
        	+ line(uv.y, 0.25, glow) + line(uv.y, 0.75, glow)
        	+ line(uv.y, 0.5, glow)
        	+ line(uv.y, 0.0, glow) + line(uv.y, 1.0, glow);   

    return c;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 umuv = (fragCoord.xy / iResolution.xy - .5) * 2.;

    // A beautiful addition!
    //umuv.y = umuv.y + sin(umuv.x * 3.14 + iTime) * 0.2;

	vec2 uv = umuv;
    float z = ( abs(uv.y ) * .8 + .2);


    uv.y *= 3.;

    uv /= z;

    float clampfinite = abs(clamp(umuv.y, -1., 0.)) * pow(z, 1. / 5.);
    float clamptop = abs(clamp(umuv.y, 0., 1.));

    uv.y += abs(iTime * 0.1);

    float grain = (0.8 + 0.2 * rand(umuv + iTime));
    vec4 color = vec4(255, 120, 153, 255) / 255.;
	fragColor = (grid(fract(uv))) * grain * color * clampfinite 
        		+ line(umuv.y, 0., 0.08) * color
        		+ color * clamptop * grain;
}
