float frequency = 40.0;
float amplitude = 0.05;

float warp(vec2 uv, bool fromCenter)
{

    float time = iTime / 4.0;
	float s = 0.0;

	s = sin((uv.y + time) * frequency);
	s = s * amplitude;

    // Build in magnitude from center
    if (fromCenter) {
        s = 1.0 + s;
        s = (uv.x - 0.5) * s + 0.5;
    // Consistent throughout
    } else {
        s *= 0.5; // For consistency when going between center to throughout
        s += uv.x;
    }

    return clamp(s, 0.0, 1.0);

}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{

	vec2 uv = fragCoord.xy / iResolution.xy;
	vec2 o = uv;

    // Show right half distorted
	if(uv.x > 0.5) {
		o.x = warp(uv, true);
	}

    // Uncomment below to show distortion over the full screen
    // o.x = warp(uv, true);

	fragColor = texture(iChannel0, o);

}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{

    vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 o = uv * 2.0;

    if(uv.y > 0.5) {
        fragColor = texture(iChannel0, o);
    } else {
        fragColor = texture(iChannel1, uv);
    }

}
