float PI = 3.141592653589;

vec4   red = vec4( 1.0 , 0.0 , 0.0 , 1.0 );
vec4 green = vec4( 0.0 , 1.0 , 0.0 , 1.0 );

void mainImage( out vec4 fragColor, in vec2 c )
{   

    float a = 0.5;
    float p = 1.0;

    vec2 uv = c / iResolution.xy ;

    // sine wave        
    float t = sin( 2.0 * uv.x * PI * p ) * a ;



    //

    t = t + 0.5;

    if( uv.y > t ) {
        fragColor = green ;
    } else {
        fragColor = red;
    }
}
