const float PI = 3.1415926; // 円周率
const float PI2 = PI * 2.0; // 円周率の二倍
const int number = 11;


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float f = 0.0;
    float a = 0.0;
    float s = 0.0;
    vec2 p = (fragCoord.xy * 2.0 - iResolution.xy) / min(iResolution.x, iResolution.y);

    if(number == 1) {
        f = 0.2 / p.y;
    }

    if(number == 2) {
        f = 0.2 / abs(p.y);
    }
    if(number == 3) {
        f = 0.1 / length(p);
    }
    if(number == 4) {
        f = 0.1 / (length(p) - 0.5);

    }
    if(number == 5) {
        f = abs(length(p) - 0.5);

    }
    if(number == 6) {
        f = 0.1 / abs(length(p) - 0.5);

    }
    if(number == 7) {
        f = atan(p.y, p.x); 
    }
    if(number == 8) {
        // 0 - 1 に変換
        f = (atan(p.y, p.x) + PI) / PI2; 
    }
    if(number == 9) {
        // 0 - 1 に変換
        a = (atan(p.y, p.x) + PI) / PI2;
        // 振動数を5倍にする
        s = sin(a * PI2 * 5.0);
        f = 0.1 / abs(length(p) - 0.5 - s * 0.1);
    }
    if(number == 10) {
        // 0 - 1 に変換
        a = (atan(p.y, p.x) + PI) / PI2;
        // 振動数を5倍にする
        s = sin(a * PI2 * 5.0);
        // 序盤のpを後で埋める。
        f = 0.1 / abs(length(p) - (0.5 + s * 0.1)) + step(length(p), 0.5 + s * 0.1);
    }
     if(number == 11) {
        // 0 - 1 に変換
        a = (atan(p.y, p.x) + PI) / PI2;
        // 振動数を5倍にする
        s = sin(a * PI2 * 100.0);
        // 序盤のpを後で埋める。
        f = 0.1 / abs(length(p) - (0.5 + s * 0.1)) + step(length(p), 0.5 + s * 0.1);
    }


    fragColor = vec4(vec3(f), 1.0);



}
