void mainImage (out vec4 fragColor, in vec2 fragCoord) {
    vec2 r = fragCoord / iResolution.xy;
    vec3 c = texture(iChannel0, r).xyz;
    float n = texture(iChannel1, r).x;

    //  Fills black -> red -> green -> blue -> white with a bit of noise to avoid large swaths filling at once
    //
    c *= step(4.0 * n + c.x * 2.0 + c.y * 4.0 + c.z * 8.0, mod(float(iFrame) / 60.0, 20.0));

    fragColor = vec4(c, 1.0);
}
