vec3 DrawCircle(in vec2 uv, in vec2 center, float radius, float blur, in vec3 backCol, in vec3 drawCol)
{
    float dis = length(uv - center);
    float rate = smoothstep(radius, radius - blur, dis);
    vec3 col = mix(backCol, drawCol, rate);
    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 red = vec3(1.0, 0.0, 0.0);
    vec3 green = vec3(0.0, 1.0, 0.0);
    vec3 blue = vec3(0.0, 0.0, 1.0);

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    uv -= 0.5;
    uv *= 2.0;  //(x,y -> [-1,1])
    uv.x *= iResolution.x / iResolution.y; //x unit equal y unit
	// head
    vec3 head = DrawCircle(uv, vec2(0.0,0.0), 0.8, 0.1, red, blue);
    // left eye  
    vec3 leftEye = DrawCircle(uv, vec2(-0.35,0.2), 0.2, 0.1, head, green);
    // right eye
    vec3 rightEye = DrawCircle(uv, vec2(0.35,0.2), 0.2, 0.1, leftEye, green);

    vec3 lastCol = rightEye;
    // Output to screen
    fragColor = vec4(lastCol,1.0);
}
