#define N 7
#define E 2.71828
#define PI 3.14159

#if (N == 5)
const float[N] ek = float[N](31.0, 26.0, 16.0, 6.0, 1.0);
#endif
#if (N == 6)
const float[N] ek = float[N](63.000000,57.000000,42.000000,22.000000,7.000000,1.000000);
#endif
#if (N == 7)
const float[N] ek = float[N](127.000000,120.000000,99.000000,64.000000,29.000000,8.000000,1.000000);
#endif

vec2 cpow(vec2 z1, vec2 z2)
{
	float a2b2 = z1.x * z1.x + z1.y * z1.y;
	float t1 = pow(a2b2, z2.x / 2.0) * exp(-z2.y * atan(z1.y, z1.x));
	float t2 = z2.x * atan(z1.y, z1.x) + 0.5 * z2.y * log(a2b2);
	return vec2(t1 * cos(t2), t1 * sin(t2));
}

vec2 cexp(vec2 z)
{
    return cpow(vec2(E, 0.0), z);
}

vec2 cdiv(vec2 z1, vec2 z2)
{
	return z1 * mat2(z2, -z2.y, z2.x) / dot(z2, z2);
}

vec2 cprod(vec2 z1, vec2 z2)
{
	return vec2(
		z1.x * z2.x - z1.y * z2.y,
		z1.x * z2.y + z1.y * z2.x);
}

vec2 alt(int x)
{
	float[2] tbl = float[2](1.0,-1.0);
	return vec2(tbl[int(mod(float(x),2.0))],0.0);
}

vec2 zeta(vec2 s)
{
	vec2 sum1 = vec2(0.0);
	vec2 sum2 = vec2(0.0);
	vec2 k;
	for(int i = 1; i <= N; i++)
	{
		k = vec2(float(i), 0.0);
		sum1 += cdiv(alt(i - 1), cpow(k , s));
	}
	for(int i = N + 1; i <= 2 * N; i++)
	{
		k = vec2(float(i), 0.0);
		sum2 += cdiv(cprod(alt(i - 1), vec2(ek[i - N - 1],0.0)), cpow(k,s));
	}
    sum2 = cdiv(sum2, vec2(pow(2.0, float(N)), 0.0));
    return cdiv(sum1 + sum2,vec2(1.0, 0.0) - cpow(vec2(2.0 ,0.0), vec2(1.0, 0.0) - s));
	return vec2(0.0);
}

vec2 iterate(vec2 s)
{
    vec2 z = s;
    for(int i = 0; i < 32; i++)
    {
        z = zeta(z);
    }
    return z;
}

vec4 gradient(float theta)
{
    vec4 color = vec4(0.0);
    color += vec4(1.0, 0.0, 0.0, 0.0) * smoothstep(0.0, 1.0, clamp(1.0 - abs((2.0 * PI-theta) * PI / 3.0), 0.0, 1.0));
    color += vec4(1.0, 0.0, 0.0, 0.0) * smoothstep(0.0, 1.0, clamp(1.0 - abs(-theta * PI / 3.0), 0.0, 1.0));
    color += vec4(1.0, 1.0, 0.0, 0.0) * smoothstep(0.0, 1.0, clamp(1.0 - abs((PI / 3.0 - theta) * PI / 3.0), 0.0, 1.0));
    color += vec4(0.0, 1.0, 0.0, 0.0) * smoothstep(0.0, 1.0, clamp(1.0 - abs((PI * 2.0 / 3.0 - theta) * PI / 3.0), 0.0, 1.0));
    color += vec4(0.0, 1.0, 1.0, 0.0) * smoothstep(0.0, 1.0, clamp(1.0 - abs((PI - theta) * PI / 3.0), 0.0, 1.0));
    color += vec4(0.0, 0.0, 1.0, 0.0) * smoothstep(0.0, 1.0, clamp(1.0 - abs((PI * 4.0 / 3.0 - theta) * PI / 3.0), 0.0, 1.0));
    color += vec4(1.0, 0.0, 1.0, 0.0) * smoothstep(0.0, 1.0, clamp(1.0 - abs((PI * 5.0 / 3.0 - theta) * PI / 3.0), 0.0, 1.0));
    return color*(fract(theta / PI * 10.0) / 2.0 + 0.5);
}

vec4 plotDomain(vec2 z)
{
    float theta = mod(atan(z.y, z.x), 2.0 * PI);
    return gradient(theta) * (fract(log2(length(z))) / 2.0 + 0.5);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float ratio = iResolution.x/iResolution.y;
    vec2 uv = vec2(
		(fragCoord.x-iResolution.x/2.0)/iResolution.x*ratio,
		(fragCoord.y-iResolution.y/2.0)/iResolution.y);
    fragColor = plotDomain(iterate(uv * 20.0 + vec2(0.0, iTime * 4.0)));
    //fragColor = plotDomain(iterate(uv));
}
